import React from 'react';
import './index.css';

class Creation extends React.Component {
    constructor(props) {
        super(props);
        this.createArticle = this.createArticle.bind(this);
        this.handleChangeTitle = this.handleChangeTitle.bind(this);
        this.handleChangeType = this.handleChangeType.bind(this);
        this.handleChangeContent = this.handleChangeContent.bind(this);
        this.handleClick = this.handleClick.bind(this);
        this.state = {
            ID: 'undef',
            title: 'undef',
            type: 'undef',
            creationDate: 'undef',
            content: 'Écrivez un essai à propos de votre élément du DOM préféré'
        };
    }
    handleChangeTitle(event) {
        this.setState({title: event.target.value});
    }
    handleChangeType(event) {
        this.setState({title: event.target.value});
    }
    handleChangeContent(event) {
        this.setState({content: event.target.value});
    }
    createArticle() {
        this.setState({
            title: 'new',
            creationDate: new Date().toLocaleString()
        });
        console.log(this.state.creationDate);
    }
    handleClick(event) {
        alert('Un essai a été envoyé : ' + JSON.stringify(this.state));
        this.props.submit(this.state);
        event.preventDefault();
    }
    render() {
        return (
            <div>
                <form onSubmit={this.handleClick}>
                    <label>
                        Article type :
                        <select value={this.state.type} onChange={this.handleChangeType}>
                            <option value="recipe">Recipe</option>
                            <option value="poem">Poem</option>
                            <option value="trip">Trip</option>
                            <option value="note">Note</option>
                        </select>
                    </label>
                    <label>
                        Title :
                        <input type="text" value={this.state.title} onChange={this.handleChangeTitle} />
                    </label>
                    <label>
                        Content:
                        <textarea value={this.state.content} onChange={this.handleChangeContent} />
                    </label>
                    <input type="submit" value="Create article" />
                </form>
                {/*Future form*/}
                {/*<button onClick={this.createArticle}>create </button>*/}
                {/*<button onClick={this.handleClick}> submit </button>*/}
            </div>
    );
    }
}
export default Creation;
