import React from 'react';
import './index.css';
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';
import Container from 'react-bootstrap/Container';


class AddIngredient extends React.Component {
    constructor(props) {
        super(props);
        this.handleChange = this.handleChange.bind(this);
        this.findIng = this.findIng.bind(this);
        // this.confirmAdd = this.confirmAdd.bind(this);
        this.state = {
            userInput: '',
            choices: []
        };
    }
    // mise à jour userInput
    handleChange(event) {
        const target = event.target;
        const name = target.name;
        this.setState({
            [name]: target.value
        });
    }
    findIng() {
        let value = this.state.userInput;
        // let ApiBase= "https://api.nal.usda.gov/fdc/v1/foods/search?query=XXX&pageSize=&api_key=tgyxI8kaDGTFx9V50jKbPXTpMnzKSEJG3c6oAZsD";
        let apiBase= "https://api.nal.usda.gov/fdc/v1/foods/search?api_key=DEMO_KEY&";
        let requestBody= {
            "query": value,
            "generalSearchInput": value,
            "includeDataTypes" : {
                "Foundation": true
            },
            "includeMarketCountries": null,
            "referenceFoodsCheckBox": true,
            "exactBrandOwner": null,
            "requireAllWords": true,
            "pageNumber": 1,
            "sortCriteria":{
                "sortColumn":"description",
                "sortDirection":"asc"
            }
        };
        // get all entities - GET
        fetch(apiBase, {
            method: 'POST',
            headers: {
                'Accept': 'application/json',
                'Content-Type': 'application/json'
            },
            body: JSON.stringify(requestBody)
        })
            .then(response => response.json())
            .then(response => {
                alert(JSON.stringify(response.foods[0]));
                let firstValue= response.foods[0];
                let nutrientFacts = {};
                firstValue.foodNutrients.forEach((nutrientFact) => {
                    switch (nutrientFact.nutrientName) {
                        case 'Protein':
                            nutrientFacts['protein']= nutrientFact.value;
                            break;
                        case 'Carbohydrate, by difference':
                            nutrientFacts['carbohydrate']= nutrientFact.value;
                            break;
                        case 'Energy (Atwater General Factors)':
                            nutrientFacts['kcal']= nutrientFact.value;
                            break;
                        case 'Total lipid (fat)':
                            nutrientFacts['lipid']= nutrientFact.value;
                            break;
                        case 'Sugars, Total NLEA':
                            nutrientFacts['sugars']= nutrientFact.value;
                            break;
                    }
                    if (!nutrientFacts['kcal'] && nutrientFact.unitName === 'KCAL') {
                        nutrientFacts['kcal']= nutrientFact.value;
                    }
                });
                let choices = [{
                    fdcId: firstValue.fdcId,
                    foodCategory: firstValue.foodCategory,
                    description: firstValue.description,
                    nutrientFacts: nutrientFacts
                }];
                this.setState({
                    choices: choices
                });
                // alert('Un ingredient a été ajouté: ' + JSON.stringify(this.state));
            })
            .catch(err => { console.log(err);});
    }
    confirmAdd(choice) {
        this.props.ing=this.state.choices[choice];
    }
    render() {
        return (
            <Container className="p-3 w-50">
                    <Form>
                        <Form.Group controlId={'compute.' + this.props.index} className="mb-3 w-50">
                            <Form.Label>Ingredient n°{this.props.index}</Form.Label>
                            <Form.Control type="text" name="userInput" placeholder={this.state.userInput} onChange={this.handleChange}/>
                        </Form.Group>
                        <Button variant="primary" onClick={this.findIng}>
                         Find ingredient
                        </Button>
                        <ul>
                            {this.state.choices.map((choice, index) => {
                                return <li key={index}>{choice.description} <button>select</button></li>
                            })}

                        </ul>
                        {/*<Button variant="primary" onClick={this.confirmAdd(0)}>*/}
                            {/*Confirm*/}
                        {/*</Button>*/}
                    </Form>
            </Container>
        );
    }
}
export default AddIngredient;
