import React from 'react';
import './index.css';
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';
import Container from 'react-bootstrap/Container';


class AddIngredient extends React.Component {
    constructor(props) {
        super(props);
        this.handleChange = this.handleChange.bind(this);
        this.handleClick = this.handleClick.bind(this);
        this.state = {
            name: 'undef',
            protein: 0,
            glucids: 0,
            lipids: 0
        };
    }
    handleChange(event) {
        const target = event.target;
        const name = target.name;
        this.setState({
            [name]: target.value
        });
    }
    handleChangeIng(event, index) {
        const target = event.target;
        const value = target.value;
        let ingredients = this.state.ingredients.slice();
        ingredients[index] = value;
        this.setState({
            ingredients: ingredients
        });
    }
    findIng(event) {
        let value = event.target.value;
        // let ApiBase= "https://api.nal.usda.gov/fdc/v1/foods/search?query=XXX&pageSize=&api_key=tgyxI8kaDGTFx9V50jKbPXTpMnzKSEJG3c6oAZsD";
        let apiBase= "https://api.nal.usda.gov/fdc/v1/foods/search?api_key=DEMO_KEY&";
        let requestBody= {
            "query": value,
            "generalSearchInput": value,
            "includeDataTypes" : {
                "Foundation": true
            },
            "includeMarketCountries": null,
            "referenceFoodsCheckBox": true,
            "exactBrandOwner": null,
            "requireAllWords": true,
            "pageNumber": 1,
            "sortCriteria":{
                "sortColumn":"description",
                "sortDirection":"asc"
            }
        };
        // get all entities - GET
        fetch(apiBase, {
            method: 'POST',
            headers: {
                'Accept': 'application/json',
                'Content-Type': 'application/json'
            },
            body: JSON.stringify(requestBody)
        })
            .then(response => response.json())
            .then(response => {
                alert(JSON.stringify(response.foods[0]));
                let firstValue= response.foods[0];
                let nutrientFacts = {};
                firstValue.foodNutrients.find()
                this.setState({
                    rawValue: firstValue,
                    fdcId: firstValue.fdcId,
                    foodCategory: firstValue.foodCategory,
                    description: firstValue.description,
                    energy: firstValue.foodNutrients
                })
            })
            .catch(err => { console.log(err);});

        alert('Une recette a été envoyé : ' + JSON.stringify(this.state));
        event.preventDefault();
    }
    handleClickIngredients(event) {
        let ingredients = this.state.ingredients.slice();
        ingredients.push(this.state.new);
        this.setState({
            ingredients: ingredients
        });
        event.preventDefault();
    }
    addIng() {
        let ingredients =this.state.ingredients.slice();
        ingredients.push('new');
        this.setState({
            ingredients: ingredients
        });
    }
    render() {
        return (
            <Container className="p-3 w-50">
                    <Form>
                        <Form.Group controlId={'compute.' + this.props.index} className="mb-3 w-50">
                            <Form.Label>Ingredient n°{this.props.index}</Form.Label>
                            <Form.Control type="text" name="new" placeholder={this.props.ing} onChange={e => this.props.handleChangeIng(e, this.props.index)}/>
                        </Form.Group>
                        {/*<Button variant="primary" onClick={this.handleClickIngredients}>*/}
                        {/*add this ingredient*/}
                        {/*</Button>*/}
                    </Form>
            </Container>
        );
    }
}
export default AddIngredient;
