import React from 'react';
import './index.css';
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';
import Container from 'react-bootstrap/Container';


class ComputeRecipe extends React.Component {
    constructor(props) {
        super(props);
        this.handleChange = this.handleChange.bind(this);
        this.handleChangeIng = this.handleChangeIng.bind(this);
        this.handleClick = this.handleClick.bind(this);
        this.addIng = this.addIng.bind(this);
        this.handleClickIngredients = this.handleClickIngredients.bind(this);
        this.state = {
            new: '',
            type: 'undef',
            ingredients: [],
            kcal: 0,
            prot: 0,
            Glu: 0,
            lip: 0
        };
    }
    handleChange(event) {
        const target = event.target;
        const name = target.name;
        this.setState({
            [name]: target.value
        });
    }
    handleChangeIng(event, index) {
        const target = event.target;
        const value = target.value;
        let ingredients = this.state.ingredients.slice();
        ingredients[index] = value;
        this.setState({
            ingredients: ingredients
        });
    }
    handleClick(event) {
        alert('Une recette a été envoyé : ' + JSON.stringify(this.state));
        event.preventDefault();
    }
    handleClickIngredients(event) {
        let ingredients = this.state.ingredients.slice();
        ingredients.push(this.state.new);
        this.setState({
            ingredients: ingredients
        });
        event.preventDefault();
    }
    addIng() {
        let ingredients =this.state.ingredients.splice();
        ingredients.push('new');
        this.setState({
            ingredients: ingredients
        });
    }
    render() {
        const newIngForm = <Form onSubmit={this.handleClickIngredients}>
            <Form.Group controlId="compute.new" className="mb-3 w-50">
                <Form.Label>Ingredient</Form.Label>
                <Form.Control type="text" name="new" placeholder={this.state.new} onChange={this.handleChange}/>
            </Form.Group>
            <Button variant="primary" type="submit">
                add this ingredient
            </Button>
        </Form>;
        return (
            <Container className="p-3 w-50">
                <Form onSubmit={this.handleClick}>
                    <Form.Group controlId="compute.one" className="w-50">
                        <Form.Label>Choose article Type</Form.Label>
                        <Form.Control
                            as="select"
                            value={this.state.type}
                            onChange={this.handleChange}
                        >
                            <option value="dessert">Dessert</option>
                            <option value="poem">Plat</option>
                        </Form.Control>
                    </Form.Group>
                    <Button variant="primary" type="submit">
                        Submit
                    </Button>
                </Form>
                {this.state.ingredients.map((ing, index) => {
                    return (
                        <Form onSubmit={this.handleClickIngredients}>
                            <Form.Group controlId={'compute.' + index} className="mb-3 w-50">
                                <Form.Label>Ingredient n°{index}</Form.Label>
                                <Form.Control type="text" name="new" placeholder={ing} onChange={event => handleChangeIng(event, index)}/>
                            </Form.Group>
                            <Button variant="primary" type="submit">
                                add this ingredient
                            </Button>
                        </Form>
                    );
                })}
                <Button variant="primary" onClick={addIng}>
                    Add ingredient
                </Button>
                <div>
                    <ul>
                    {this.state.ingredients.map((ing) => {
                        return <li>{ing}</li>
                    })}
                    </ul>
                </div>
            </Container>
        );
    }
}
export default ComputeRecipe;
