import React from 'react';
import './index.css';
import Form from 'react-bootstrap/Form';
import Modal from 'react-bootstrap/Modal';
import Button from 'react-bootstrap/Button';
import Container from 'react-bootstrap/Container';


class AddIngredient extends React.Component {
    constructor(props) {
        super(props);
        this.handleChange = this.handleChange.bind(this);
        this.handleShow = this.handleShow.bind(this);
        this.handleClose = this.handleClose.bind(this);
        this.state = {
            show: false,
            userInput: '',
            choices: []
        };
    }
    handleShow() {
        this.setState({show: true});
    }
    handleClose() {
        this.setState({show: false});
    }
    // mise à jour userInput
    handleChange(event) {
        const target = event.target;
        const name = target.name;
        this.setState({
            [name]: target.value
        });
    }
    render() {
        let showSelection;
        if (this.props.ing.value) {
            showSelection = <p> {this.props.ing.value.description} {this.props.ing.value.nutrientFacts.kcal} Kcal, {this.props.ing.value.nutrientFacts.carbohydrate} G, {this.props.ing.value.nutrientFacts.protein} P, {this.props.ing.value.nutrientFacts.lipid} L </p>
        }
        return (
            <Container className="p-3 w-50">
                <Modal show={this.state.show} onHide={this.handleClose}>
                    <Modal.Header closeButton>
                        <Modal.Title>Modal heading</Modal.Title>
                    </Modal.Header>
                    <Modal.Body>
                        <ul>
                            {!this.props.ing.value && this.props.ing.choices && this.props.ing.choices.map((choice, index) => {
                                return <li key={'choices.' + index}>{choice.description} <button onClick={e => this.props.selectChoice(e, this.props.index, index)}>select</button></li>
                            })}
                        </ul>
                    </Modal.Body>
                    <Modal.Footer>
                        <Button variant="secondary" onClick={this.handleClose}>
                            Close
                        </Button>
                        <Button variant="primary" onClick={this.handleClose}>
                            Save Changes
                        </Button>
                    </Modal.Footer>
                </Modal>
                    {/*<Form>*/}
                        <Form.Group controlId={'compute.' + this.props.index} className="mb-3 w-50">
                            <Form.Label>Ingredient n°{this.props.index}</Form.Label>
                            <Form.Control type="text" name="userInputName" placeholder={this.props.ing.userInputName} onChange={e => this.props.handleChangeIng(e, this.props.index)}/>
                            <Form.Control type="text" name="userInputQuantity" placeholder={this.props.ing.userInputQuantity} onChange={e => this.props.handleChangeIng(e, this.props.index)}/>
                        </Form.Group>
                        {/*<Button variant="primary" onClick={e => this.props.findIng(e, this.props.index)}>*/}
                         {/*Find*/}
                        {/*</Button>*/}
                        <Button variant="primary" onClick={this.handleShow}>
                            Launch demo modal
                        </Button>
                        <Button variant="danger" onClick={e => this.props.removeIng(e, this.props.index)}>
                            Remove
                        </Button>

                        {showSelection}
                    {/*</Form>*/}
            </Container>
        );
    }
}
export default AddIngredient;
