import React from 'react';
import './index.css';

class Creation extends React.Component {
    constructor(props) {
        super(props);
        this.createArticle = this.createArticle.bind(this);
        this.handleChange = this.handleChange.bind(this);
        this.handleChangeContent = this.handleChangeContent.bind(this);
        this.handleClick = this.handleClick.bind(this);
        this.state = {
            ID: 'undef',
            title: 'undef',
            type: 'undef',
            creationDate: 'undef',
            content: 'Écrivez un essai à propos de votre élément du DOM préféré'
        };
    }
    handleChange(event) {
        this.setState({title: event.target.value});
    }
    handleChangeContent(event) {
        this.setState({content: event.target.value});
    }
    createArticle() {
        this.setState({
            title: 'new',
            creationDate: new Date().toLocaleString()
        });
        console.log(this.state.creationDate);
    }
    handleClick(event) {
        alert('Un essai a été envoyé : ' + JSON.stringify(this.state));
        this.props.submit(this.state);
        event.preventDefault();
    }
    render() {
        return (
            <div>
                <form onSubmit={this.handleClick}>
                    <label>
                        Titre :
                        <input type="text" value={this.state.title} onChange={this.handleChange} />
                    </label>
                    <label>
                        Content:
                        <textarea value={this.state.content} onChange={this.handleChangeContent} />
                    </label>
                    <input type="submit" value="Envoyer" />
                </form>
                {/*Future form*/}
                {/*<button onClick={this.createArticle}>create </button>*/}
                <button onClick={this.handleClick}> submit </button>
            </div>
    );
    }
}
export default Creation;
