import React from 'react';
import './index.css';
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';
import Container from 'react-bootstrap/Container';


class AddIngredient extends React.Component {
    constructor(props) {
        super(props);
        this.handleChange = this.handleChange.bind(this);
        this.findIng = this.findIng.bind(this);
        // this.confirmAdd = this.confirmAdd.bind(this);
        this.state = {
            userInput: '',
            choices: []
        };
    }
    // mise à jour userInput
    handleChange(event) {
        const target = event.target;
        const name = target.name;
        this.setState({
            [name]: target.value
        });
    }
    confirmAdd(choice) {
        this.props.ing=this.state.choices[choice];
    }
    render() {
        return (
            <Container className="p-3 w-50">
                    <Form>
                        <Form.Group controlId={'compute.' + this.props.index} className="mb-3 w-50">
                            <Form.Label>Ingredient n°{this.props.index}</Form.Label>
                            <Form.Control type="text" name="userInput" placeholder={this.state.userInput} onChange={this.handleChange}/>
                        </Form.Group>
                        <Button variant="primary" onClick={this.props.findIng}>
                         Find ingredient
                        </Button>
                        <ul>
                            {this.props.ing.choices.map((choice, index) => {
                                return <li key={index}>{choice.description} <button onClick={this.props.selectChoice(this.props.index, index)}>select</button></li>
                            })}

                        </ul>
                        <Button variant="primary" onClick={this.confirmAdd(this.props.index, 0)}>
                            Confirm
                        </Button>
                    </Form>
            </Container>
        );
    }
}
export default AddIngredient;
