import './App.scss';
// import Recipe from './Recipe';
import Creation from './Creation';
import ListArticles from './ListArticles';
import Article from './Article';
import Header from './Header';
import ComputeRecipe from './ComputeRecipe';
import Button from 'react-bootstrap/Button';
import Navbar from 'react-bootstrap/Navbar';
import Container from 'react-bootstrap/Container';
import React from "react";
import {
    BrowserRouter as Router,
    Switch,
    Route,
    Link
} from "react-router-dom";
import NavDropdown from "react-bootstrap/NavDropdown";

class App extends React.Component {
    constructor(props) {
        super(props);
        this.submit = this.submit.bind(this);
        this.state = {
            articles: []
        };
    }
    submit(article) {
        let articles = this.state.articles.slice();
        article.ID = articles.length;
        article.creationDate = new Date().toLocaleString();
        articles.push(article);
        this.setState({
            articles: articles
        });
    }
    render() {
        return (
            <div className="App">
                <header className="App-header">
                    <p>
                        Life Diary.
                    </p>
                </header>
                <div>
                    <Router>
                        {/*Routing*/}
                        <Switch>
                            <Route path="/computeRecipe">
                                <ComputeRecipe />
                            </Route>
                            <Route path="/articles/new">
                                <Creation submit={this.submit} />
                            </Route>
                            <Route path="/articles/:id">
                                <Article />
                            </Route>
                            <Route path="/articles">
                                <ListArticles />
                            </Route>
                            <Route path="/">
                                <ListArticles articles={this.state.articles}/>
                            </Route>
                        </Switch>
                        <Header/>
                        <div>
                            <ul>
                                <li>
                                    <Link to="/">Home</Link>
                                </li>
                                <li>
                                    <Link to="/computeRecipe">computeRecipe</Link>
                                </li>
                                <li>
                                    <Link to="/articles">articles</Link>
                                </li>
                            </ul>
                        </div>
                    </Router>
                </div>
                {/*<Menu></Menu>*/}
                {/*<Recipe/>*/}
                {/*<Creation submit={this.submit}/>*/}
                {/*<ListArticles articles={this.state.articles}/>*/}
            </div>
        );
    }
}

export default App;
