import React from 'react';
import './index.css';
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';
import Container from 'react-bootstrap/Container';


class ComputeRecipe extends React.Component {
    constructor(props) {
        super(props);
        this.handleChange = this.handleChange.bind(this);
        this.handleChangeIng = this.handleChangeIng.bind(this);
        this.handleClick = this.handleClick.bind(this);
        this.addIng = this.addIng.bind(this);
        this.handleClickIngredients = this.handleClickIngredients.bind(this);
        this.state = {
            new: '',
            title: 'undef',
            type: 'undef',
            ingredients: [],
            kcal: 0,
            prot: 0,
            Glu: 0,
            lip: 0
        };
    }
    handleChange(event) {
        const target = event.target;
        const name = target.name;
        this.setState({
            [name]: target.value
        });
    }
    handleChangeIng(event, index) {
        const target = event.target;
        const value = target.value;
        let ingredients = this.state.ingredients.slice();
        ingredients[index] = value;
        this.setState({
            ingredients: ingredients
        });
    }
    handleClick(event) {
        alert('Une recette a été envoyé : ' + JSON.stringify(this.state));
        event.preventDefault();
    }
    handleClickIngredients(event) {
        let ingredients = this.state.ingredients.slice();
        ingredients.push(this.state.new);
        this.setState({
            ingredients: ingredients
        });
        event.preventDefault();
    }
    addIng() {
        let value = "apple";
        // let ApiBase= "https://api.nal.usda.gov/fdc/v1/foods/search?query=XXX&pageSize=&api_key=tgyxI8kaDGTFx9V50jKbPXTpMnzKSEJG3c6oAZsD";
        let apiBase= "https://api.nal.usda.gov/fdc/v1/foods/search?api_key=DEMO_KEY&";
        let requestBody= {
            "query": value,
            "generalSearchInput": value,
            "includeDataTypes" : {
                "Foundation": true
            },
            "includeMarketCountries": null,
            "referenceFoodsCheckBox": true,
            "exactBrandOwner": null,
            "requireAllWords": true,
            "pageNumber": 1,
            "sortCriteria":{
                "sortColumn":"description",
                "sortDirection":"asc"
            }
        };
        // get all entities - GET
        fetch(apiBase, {
            "method": "POST",
            "body": requestBody
        })
            .then(response => alert(response.json()))
            .then(response => {
                this.setState({
                    friends: response
                })
            })
            .catch(err => { console.log(err);
            });
        let ingredients =this.state.ingredients.slice();
        ingredients.push('new');
        this.setState({
            ingredients: ingredients
        });
    }
    render() {
        return (
            <Container className="p-3 w-50">
                <Form onSubmit={this.handleClick}>
                    <Form.Group controlId="compute.type" className="w-50">
                        <Form.Label>Choose article Type</Form.Label>
                        <Form.Control
                            as="select"
                            name="type"
                            value={this.state.type}
                            onChange={this.handleChange}
                        >
                            <option value="choose">Choose</option>
                            <option value="dessert">Dessert</option>
                            <option value="plat">Plat</option>
                        </Form.Control>
                    </Form.Group>
                    <Form.Group controlId='compute.title' className="mb-3 w-50">
                        <Form.Label>Titre de la recette</Form.Label>
                        <Form.Control type="text" name="title" placeholder={this.state.title} onChange={this.handleChange}/>
                    </Form.Group>
                {this.state.ingredients.map((ing, index) => {
                    return (
                        <Form>
                            <Form.Group controlId={'compute.' + index} className="mb-3 w-50">
                                <Form.Label>Ingredient n°{index}</Form.Label>
                                <Form.Control type="text" name="new" placeholder={ing} onChange={e => this.handleChangeIng(e, index)}/>
                            </Form.Group>
                            {/*<Button variant="primary" onClick={this.handleClickIngredients}>*/}
                                {/*add this ingredient*/}
                            {/*</Button>*/}
                        </Form>
                    );
                })}
                    <Button variant="primary" onClick={this.addIng} className="m-3">
                        Add ingredient
                    </Button>
                    <Button variant="danger" type="submit" className="m-3">
                        create recipe
                    </Button>
                </Form>

                <div>
                    <p>La recette de {this.state.title}</p>
                    <i>{this.state.type}</i>
                    <ul>
                        {this.state.ingredients.map((ing) => {
                            return <li>{ing}</li>
                        })}
                    </ul>
                </div>
            </Container>
        );
    }
}
export default ComputeRecipe;
