import React from 'react';
import './index.css';
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';
import Container from 'react-bootstrap/Container';
import AddIngredient from './AddIngredient.js';


class ComputeRecipe extends React.Component {
    constructor(props) {
        super(props);
        this.handleChange = this.handleChange.bind(this);
        this.handleChangeIng = this.handleChangeIng.bind(this);
        this.handleClick = this.handleClick.bind(this);
        this.addIng = this.addIng.bind(this);
        this.selectChoice = this.selectChoice.bind(this);
        this.findIng = this.findIng.bind(this);
        this.state = {
            new: '',
            title: 'undef',
            type: 'undef',
            ingredients: [],
            kcal: 0,
            prot: 0,
            Glu: 0,
            lip: 0
        };
    }
    //Confirmrecipe: clean choices, calculate overall nutrtional facts
    handleChange(event) {
        const target = event.target;
        const name = target.name;
        this.setState({
            [name]: target.value
        });
    }
    handleChangeIng(event, index) {
        const target = event.target;
        const value = target.value;
        let ingredients = this.state.ingredients.slice();
        ingredients[index].userInput = value;
        this.setState({
            ingredients: ingredients
        });
    }
    handleClick(event) {
        alert('Une recette a été envoyé : ' + JSON.stringify(this.state));
        event.preventDefault();
    }
    addIng() {
        let ingredients =this.state.ingredients.slice();
        ingredients.push({
            userInput: '',
            choices: []
        });
        this.setState({
            ingredients: ingredients
        });
    }
    findIng(event, ingNumber) {
        let value = this.state.ingredients[ingNumber].userInput;
        // let ApiBase= "https://api.nal.usda.gov/fdc/v1/foods/search?query=XXX&pageSize=&api_key=tgyxI8kaDGTFx9V50jKbPXTpMnzKSEJG3c6oAZsD";
        let apiBase= "https://api.nal.usda.gov/fdc/v1/foods/search?api_key=DEMO_KEY&";
        let requestBody= {
            "query": value,
            "generalSearchInput": value,
            "includeDataTypes" : {
                "Foundation": true
            },
            "includeMarketCountries": null,
            "referenceFoodsCheckBox": true,
            "exactBrandOwner": null,
            "requireAllWords": true,
            "pageNumber": 1,
            "sortCriteria":{
                "sortColumn":"description",
                "sortDirection":"asc"
            }
        };
        // get all entities - GET
        fetch(apiBase, {
            method: 'POST',
            headers: {
                'Accept': 'application/json',
                'Content-Type': 'application/json'
            },
            body: JSON.stringify(requestBody)
        })
            .then(response => response.json())
            .then(response => {
                let firstValue= response.foods[0];
                let nutrientFacts = {};
                firstValue.foodNutrients.forEach((nutrientFact) => {
                    switch (nutrientFact.nutrientName) {
                        case 'Protein':
                            nutrientFacts['protein']= nutrientFact.value;
                            break;
                        case 'Carbohydrate, by difference':
                            nutrientFacts['carbohydrate']= nutrientFact.value;
                            break;
                        case 'Energy (Atwater General Factors)':
                            nutrientFacts['kcal']= nutrientFact.value;
                            break;
                        case 'Total lipid (fat)':
                            nutrientFacts['lipid']= nutrientFact.value;
                            break;
                        case 'Sugars, Total NLEA':
                            nutrientFacts['sugars']= nutrientFact.value;
                            break;
                    }
                    if (!nutrientFacts['kcal'] && nutrientFact.unitName === 'KCAL') {
                        nutrientFacts['kcal']= nutrientFact.value;
                    }
                });
                let choices = [{
                    fdcId: firstValue.fdcId,
                    foodCategory: firstValue.foodCategory,
                    description: firstValue.description,
                    nutrientFacts: nutrientFacts
                }];
                let ings = this.state.ingredients.slice();
                ings[ingNumber].choices = choices;
                this.setState({
                    ingredients: ings
                });
                alert('Un ingredient a été ajouté: ' + JSON.stringify(this.state.ingredients));
            })
            .catch(err => { console.log(err);});
    }
    selectChoice(event, ingNumber, choiceNumber) {
        let ings = this.state.ingredients.slice();
        ings[ingNumber]['value'] = ings[ingNumber].choices[choiceNumber];
        this.setState({
            ingredients: ings
        });
    }
    render() {
        return (
            <Container className="p-3 w-50">
                <Form onSubmit={this.handleClick}>
                    <Form.Group controlId="compute.type" className="w-50">
                        <Form.Label>Choose article Type</Form.Label>
                        <Form.Control
                            as="select"
                            name="type"
                            value={this.state.type}
                            onChange={this.handleChange}
                        >
                            <option value="choose">Choose</option>
                            <option value="dessert">Dessert</option>
                            <option value="plat">Plat</option>
                        </Form.Control>
                    </Form.Group>
                    <Form.Group controlId='compute.title' className="mb-3 w-50">
                        <Form.Label>Titre de la recette</Form.Label>
                        <Form.Control type="text" name="title" placeholder={this.state.title} onChange={this.handleChange}/>
                    </Form.Group>
                {this.state.ingredients.map((ing, index) => {
                    return (
                        <AddIngredient ing={ing} index={index} handleChangeIng={this.handleChangeIng} findIng={this.findIng} selectChoice={this.selectChoice} />
                    );
                })}
                    <Button variant="primary" onClick={this.addIng} className="m-3">
                        Add ingredient
                    </Button>
                    <Button variant="danger" type="submit" className="m-3">
                        create recipe
                    </Button>
                </Form>

                <div>
                    <p>La recette de {this.state.title}</p>
                    <i>{this.state.type}</i>
                    <ul>
                        {this.state.ingredients.map((ing) => {
                            return ing.value ? <li>{ing.value.description}</li> : <li> choose ingredients</li>
                        })}
                    </ul>
                </div>
            </Container>
        );
    }
}
export default ComputeRecipe;
