import React from 'react';
import './index.css';
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';
import Container from 'react-bootstrap/Container';


class AddIngredient extends React.Component {
    constructor(props) {
        super(props);
        this.handleChange = this.handleChange.bind(this);
        this.state = {
            userInput: '',
            choices: []
        };
    }
    // mise à jour userInput
    handleChange(event) {
        const target = event.target;
        const name = target.name;
        this.setState({
            [name]: target.value
        });
    }
    render() {
        let showSelection;
        if (this.props.ing.value) {
            showSelection = <p> {this.props.ing.value.description} {this.props.ing.value.nutrientFacts.kcal} Kcal, {this.props.ing.value.nutrientFacts.carbohydrate} G, {this.props.ing.value.nutrientFacts.proteins} P, {this.props.ing.value.nutrientFacts.lipids} L </p>
        }
        return (
            <Container className="p-3 w-50">
                    <Form>
                        <Form.Group controlId={'compute.' + this.props.index} className="mb-3 w-50">
                            <Form.Label>Ingredient n°{this.props.index}</Form.Label>
                            <Form.Control type="text" name="userInput" placeholder={this.props.ing.userInput} onChange={e => this.props.handleChangeIng(e, this.props.index)}/>
                        </Form.Group>
                        <Button variant="primary" onClick={e => this.props.findIng(e, this.props.index)}>
                         Find ingredient
                        </Button>
                        <ul>
                            {!this.props.ing.value && this.props.ing.choices && this.props.ing.choices.map((choice, index) => {
                                return <li key={index}>{choice.description} <button onClick={e => this.props.selectChoice(e, this.props.index, index)}>select</button></li>
                            })}

                        </ul>
                        {showSelection}
                    </Form>
            </Container>
        );
    }
}
export default AddIngredient;
