import './App.scss';
// import Recipe from './Recipe';
import Creation from './Creation';
import ListArticles from './ListArticles';
import Article from './Article';
import ComputeRecipe from './ComputeRecipe';
import Button from 'react-bootstrap/Button';
import NavBar from 'react-bootstrap/Button';
import React from "react";
import {
    BrowserRouter as Router,
    Switch,
    Route,
    Link
} from "react-router-dom";

class App extends React.Component {
    constructor(props) {
        super(props);
        this.submit = this.submit.bind(this);
        this.state = {
            articles: []
        };
    }
    submit(article) {
        let articles = this.state.articles.slice();
        article.ID = articles.length;
        article.creationDate = new Date().toLocaleString();
        articles.push(article);
        this.setState({
            articles: articles
        });
    }
    render() {
        return (
            <div className="App">
                <Navbar bg="light" expand="lg">
                    <Container>
                        <Navbar.Brand href="#home">React-Bootstrap</Navbar.Brand>
                        <Navbar.Toggle aria-controls="basic-navbar-nav" />
                        <Navbar.Collapse id="basic-navbar-nav">
                            <Nav className="me-auto">
                                <Nav.Link href="#home">Home</Nav.Link>
                                <Nav.Link href="#link">Link</Nav.Link>
                                <NavDropdown title="Dropdown" id="basic-nav-dropdown">
                                    <NavDropdown.Item href="#action/3.1">Action</NavDropdown.Item>
                                    <NavDropdown.Item href="#action/3.2">Another action</NavDropdown.Item>
                                    <NavDropdown.Item href="#action/3.3">Something</NavDropdown.Item>
                                    <NavDropdown.Divider />
                                    <NavDropdown.Item href="#action/3.4">Separated link</NavDropdown.Item>
                                </NavDropdown>
                            </Nav>
                        </Navbar.Collapse>
                    </Container>
                </Navbar>
                <header className="App-header">
                    <p>
                        Life Diary.
                    </p>
                </header>
                <div>
                    <Router>
                        <div>
                            <ul>
                                <li>
                                    <Link to="/">Home</Link>
                                </li>
                                <li>
                                    <Link to="/computeRecipe">computeRecipe</Link>
                                </li>
                                <li>
                                    <Link to="/articles">articles</Link>
                                </li>
                            </ul>
                        <Switch>
                            <Route path="/computeRecipe">
                                <ComputeRecipe />
                            </Route>
                            <Route path="/articles/new">
                                <Creation submit={this.submit} />
                            </Route>
                            <Route path="/articles/:id">
                                <Article />
                            </Route>
                            <Route path="/articles">
                                <ListArticles />
                            </Route>

                            <Route path="/">
                                <ListArticles articles={this.state.articles}/>
                            </Route>
                        </Switch>
                        </div>
                    </Router>
                </div>
                {/*<Menu></Menu>*/}
                {/*<Recipe/>*/}
                {/*<Creation submit={this.submit}/>*/}
                {/*<ListArticles articles={this.state.articles}/>*/}
            </div>
        );
    }
}

export default App;
